import { useQuiz } from "../Contexts/QuizContext";
import Options from "./Options";
function Question() {
  const { questions, index } = useQuiz();

  const question = questions.at(index);
  return (
    <div>
      <h4>{question}</h4>
      <Options />
    </div>
  );
}

export default Question;
