import { useEffect, useReducer } from "react";
import Header from "./Header";
import Main from "./Main";

const initialState = {
  questions: [],

  // status: loading, error, ready, active, finished
  status: "Loading",
};
function reducer(state, action) {
  switch (action.type) {
